import React, { useState } from "react";

import {
  StyleSheet,
  Dimensions,
  Text,
  Image,
  View,
  ImageBackground,
} from "react-native";
import { Block, theme } from "galio-framework";
import { TouchableOpacity } from "react-native-gesture-handler";

const { width } = Dimensions.get("screen");
const SearchButton = ({ isWhite, style, navigation }) => (
  <TouchableOpacity
    style={[styles.button, style]}
    onPress={() => navigation.navigate("Pro")}
  >
    <Icon
      size={16}
      family="Galio"
      name="search-zoom-in"
      color={theme.COLORS[isWhite ? "WHITE" : "ICON"]}
    />
  </TouchableOpacity>
);

class Home extends React.Component {
  render() {
    return (
      <>
        <View style={styles.background}>
          <Text style={styles.header}>Top Brands</Text>
        </View>

        <View style={styles.itemContainer}>
          <ImageBackground
            source={require("../assets/imgs/Cosmetic.png")}
            style={styles.image}
            resizeMode="cover"
          >
            <Text
              style={styles.text}
              onPress={() => {
                this.props.navigation.navigate("Account", {
                  screen: "price",
                  params: { user: "jane" },
                });
              }}
            >
              Cosmetic
            </Text>
          </ImageBackground>

          <ImageBackground
            source={require("../assets/imgs/Electronic.png")}
            style={styles.image}
            resizeMode="cover"
          >
            <Text style={styles.text}>Electronic</Text>
          </ImageBackground>
        </View>
        <View style={styles.itemContainer}>
          <ImageBackground
            source={require("../assets/imgs/Garments.png")}
            style={styles.image}
            resizeMode="cover"
          >
            <Text style={styles.text}>Garments</Text>
          </ImageBackground>
          <ImageBackground
            source={require("../assets/imgs/Grocery.png")}
            style={styles.image}
            resizeMode="cover"
          >
            <Text style={styles.text}>Grocery</Text>
          </ImageBackground>
        </View>
      </>
    );
  }
}

const styles = StyleSheet.create({
  itemContainer: {
    flexDirection: "row",
    flex: 2,
    backgroundColor: "#ededed",
  },
  header: {
    color: "black",
    fontSize: 24,
    fontWeight: "bold",
  },
  background: {
    flexDirection: "column",
    backgroundColor: "#ededed",
  },
  image: {
    flex: 1,
    justifyContent: "center",
    borderColor: "#000",
    marginHorizontal: 7,
    marginVertical: 10,
  },

  images: {
    width: "50%",
    height: "100%",
    borderColor: "#000",
    marginHorizontal: 7,
    marginVertical: 10,
  },
  text: {
    color: "white",
    fontSize: 24,
    lineHeight: 44,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#000000c0",
  },
});

export default Home;
