import React from "react";
import {
  StyleSheet,
  View,
  Dimensions,
  SafeAreaView,
  StatusBar,
} from "react-native";
import { FlatList, TextInput } from "react-native-gesture-handler";
import { Block, Text, Input } from "galio-framework";

const { width } = Dimensions.get("screen");
const DATA = [
  {
    id: "1",
    title: "Jumper",
  },
  {
    id: "2",
    title: "Sneakers",
  },
  {
    id: "3",
    title: "Hoodie",
  },
  {
    id: "4",
    title: "Vest",
  },
  {
    id: "5",
    title: "High heels",
  },
  {
    id: "6",
    title: "Handbag",
  },
  {
    id: "7",
    title: "Boots",
  },
  {
    id: "8",
    title: "Shorts",
  },
  {
    id: "9",
    title: "Polo shirt",
  },
  {
    id: "10",
    title: "Dress",
  },
  {
    id: "12",
    title: "Trench coat",
  },
  {
    id: "13",
    title: "Winter coat",
  },
  {
    id: "14",
    title: "Scarf",
  },
  {
    id: "15",
    title: "Cap",
  },
  {
    id: "16",
    title: "Straw hat",
  },
];
const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
  </View>
);

const Garments = () => {
  const renderItem = ({ item }) => <Item title={item.title} />;
  const [number, onChangeNumber] = React.useState(null);

  return (
    <SafeAreaView style={styles.container}>
      <View>
        <Text style={styles.head}>Garments</Text>
        <View>
          <TextInput
            style={styles.input}
            onChangeText={onChangeNumber}
            value={number}
            placeholder="search"
            keyboardType="default"
          />
        </View>
      </View>
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  head: {
    marginTop: 20,
    color: "blue",
    fontSize: 24,
    fontWeight: "bold",
    marginLeft: 15,
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 10,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 20,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: 150,
  },
});
export default Garments;
