import React from "react";

import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Dimensions,
  TouchableOpacity,
  Modal
} from "react-native";
import { theme } from "galio-framework";
import Cosmetics from "../assets/imgs/Cosmetic.png";
import Electronic from "../assets/imgs/Electronic.png";
import Garments from "../assets/imgs/Garments.png";
import Grocery from "../assets/imgs/Grocery.png";

const SearchButton = ({ isWhite, style, navigation }) => (
  <TouchableOpacity
    style={[styles.button, style]}
    onPress={() => navigation.navigate("Pro")}
  >
    <Icon
      size={16}
      family="Galio"
      name="search-zoom-in"
      color={theme.COLORS[isWhite ? "WHITE" : "ICON"]}
    />
  </TouchableOpacity>
);

const categories = [
  {
    img: Cosmetics,
    category: "cosmetics",
  },
  {
    img: Electronic,
    category: "electronic",
  },
  {
    img: Garments,
    category: "garments",
  },
  {
    img: Grocery,
    category: "grocery",
  },
];

const windowWidth = Dimensions.get("window").width;
const windowHeight = Dimensions.get("window").height;

class Home extends React.Component {
  render() {
    return (
      <>
        <View style={styles.background}>
          <Text style={styles.header}>Top Brands</Text>
        </View>

        <View style={styles.itemContainer}>
          {categories.map((item, index) => {
            return (
              <TouchableOpacity
                onPress={() => {
                  this.props.navigation.navigate("price", {
                    target: item.category,
                  });
                }}
                key={index}
              >
                <ImageBackground
                  source={item.img}
                  style={styles.image}
                  resizeMode="cover"
                >
                  <Text style={styles.text}>{item.category}</Text>
                </ImageBackground>
              </TouchableOpacity>
            );
          })}
        </View>
        <Modal>

        </Modal>
      </>
    );
  }
}

const styles = StyleSheet.create({
  itemContainer: {
    flexDirection: "row",
    flex: 1,
    backgroundColor: "#ededed",
    flexWrap: "wrap",
  },
  header: {
    color: "black",
    fontSize: 24,
    fontWeight: "bold",
  },
  background: {
    flexDirection: "column",
    backgroundColor: "#ededed",
  },
  image: {
    width: windowWidth / 2,
    height: windowHeight / 2,
  },

  text: {
    color: "white",
    fontSize: 24,
    lineHeight: 44,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#000000c0",
  },
});

export default Home;
