import React from "react";

import {
  StyleSheet,
  Dimensions,
  Text,
  View,
  ImageBackground,
} from "react-native";
import { Block, theme } from "galio-framework";
import { TouchableOpacity } from "react-native-gesture-handler";

const SearchButton = ({ isWhite, style, navigation }) => (
  <TouchableOpacity
    style={[styles.button, style]}
    onPress={() => navigation.navigate("Pro")}
  >
    <Icon
      size={16}
      family="Galio"
      name="search-zoom-in"
      color={theme.COLORS[isWhite ? "WHITE" : "ICON"]}
    />
  </TouchableOpacity>
);

const categories = [
  {
    img: "../assets/imgs/Cosmetic.png",
    category: "cosmetics",
  },
  {
    img: "../assets/imgs/Electronic.png",
    category: "electronic",
  },
  {
    img: "../assets/imgs/Garments.png",
    category: "garments",
  },
  {
    img: "../assets/imgs/Grocery.png",
    category: "grocery",
  },
];

class Home extends React.Component {
  render() {
    return (
      <>
        <View style={styles.background}>
          <Text style={styles.header}>Top Brands</Text>
        </View>

        <View style={styles.itemContainer}>
          {categories.map((item, index) => {
            return (
              <ImageBackground
                source={item.img}
                style={styles.image}
                resizeMode="cover"
              >
                <Text
                  style={styles.text}
                  onPress={() => {
                    this.props.navigation.navigate("price", {
                      target: item.category,
                    });
                  }}
                >
                  {item.category}
                </Text>
              </ImageBackground>
            );
          })}
        </View>
      </>
    );
  }
}

const styles = StyleSheet.create({
  itemContainer: {
    flexDirection: "row",
    flex: 1,
    backgroundColor: "#ededed",
    flexWrap: "wrap",
  },
  header: {
    color: "black",
    fontSize: 24,
    fontWeight: "bold",
  },
  background: {
    flexDirection: "column",
    backgroundColor: "#ededed",
  },
  image: {
    width: "50%",
    height : "50%F"
  },

  images: {
    width: "50%",
    height: "100%",
    borderColor: "#000",
    marginHorizontal: 7,
    marginVertical: 10,
  },
  text: {
    color: "white",
    fontSize: 24,
    lineHeight: 44,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#000000c0",
  },
});

export default Home;
